var giftImageUrl = "data:image/svg+xml;base64,"
var chartName= ['五保', '低保', '残疾', '失独', '重点优抚', '突出贡献'];
var chartData= ['10','20','30','15','15','10']
var data=[]
var legendName=[]
for (var i=0;i<chartData.length;i++){
    var c={
        value:chartData[i],
        name:chartName[i]+chartData[i]+'%'
    }
    data[i]=c;
    legendName[i]=chartName[i]+chartData[i]+'%';
}
option = {
    backgroundColor: '#fff',
    tooltip: {
        trigger: 'item',
        formatter: "{b} : {d}% <br/> {c}"
    },
    graphic: {
        elements: [{
            type: 'image',
            style: {
                image: giftImageUrl,
                width: 110,
                height: 120
            },
            left: 'center',
            top: 'center'
        }]
    },
    legend: {
        orient: 'vertical',
        x: '70%',
        y:'center',
        itemWidth: 20,
        itemHeight: 20,
        align: 'left',
        textStyle: {
            fontSize:14,
            color: '#000'
        },
        data: legendName
    },
    series: [{
        type: 'pie',
        radius: ['27%', '45%'],
        center: ['50%', '50%'],
        color: ['#0E7CE2', '#FF8352', '#E271DE', '#F8456B', '#00FFFF', '#4AEAB0'],
        data: data,
        labelLine: {
            normal: {
                show: false,
                length: 20,
                length2: 20,
                lineStyle: {
                    color: '#12EABE',
                    width: 2
                }
            }
        },
        label: {
            normal: {
                show:false,
                formatter: '{c|{c}}\n{hr|}\n{d|{d}%}',
                rich: {
                    b: {
                        fontSize: 20,
                        color: '#12EABE',
                        align: 'left',
                        padding: 4
                    },
                    hr: {
                        borderColor: '#12EABE',
                        width: '100%',
                        borderWidth: 2,
                        height: 0
                    },
                    d: {
                        fontSize: 20,
                        color: '#fff',
                        align: 'left',
                        padding: 4
                    },
                    c: {
                        fontSize: 20,
                        color: '#fff',
                        align: 'left',
                        padding: 4
                    }
                }
            }
        }
    }]
};