var treeUrl = 'data:image/png;base64,';

var lineCount = 4;
var cateData = [];
var series = [];
var data = [];

for (var i = 0; i < lineCount; i++) {
    cateData.push(i + '');
    data.push({
        value: 1,
        symbolOffset: (i % 2) ? ['50%', 0] : null
    });
}

option = {
    color: ['#e54035'],
    title:{
        text:'无纸化办公后每年节省约21000棵树'
    },
    xAxis: {
        show: false
    },
    yAxis: {
        data: cateData,
        show: false
    },
    grid: {
        top: 10,
        bottom: 10,
        left: 10,
        right: 10
    },
    series: [{
        name: 'all',
        type: 'pictorialBar',
        symbol: 'image://' + treeUrl,
        symbolSize: [30, 50],
        symbolRepeat: true,
        data: data,

        animationEasing: 'elasticOut'
    }]
};