var img =
  'data:image/png;base64,';
var colorList = ['#20b3ff', '#fcb55b', '#2bfed5', '#2cff8c', '#fcf65a'];
var datas = [
  {
    value: 60,
    name: '系列一',
  },
  {
    value: 52,
    name: '系列二',
  },
  {
    value: 48,
    name: '系列三',
  },
  {
    value: 36,
    name: '系列四',
  },
  {
    value: 30,
    name: '系列五',
  },
];
let maxArr = new Array(datas.length).fill(datas[0].value * 2);
option = {
  backgroundColor: '#000',
  title: {
    text: '各系列销量排名',
    bottom: 5,
    left: 'center',
    textStyle: {
      color: '#fff',
      fontSize: 12,
    },
  },
  tooltip: {
    show: true
  },
  legend: {
    show: false,
  },
  grid: {
    left: -40,
    right: 20,
    containLabel: true,
  },
  xAxis: {
    show: false,
    type: 'value',
  },
  yAxis: [
    {
      type: 'category',
      inverse: true,
      axisLine: {
        show: false,
      },
      axisTick: {
        show: false,
      },
      axisPointer: {
        label: {
          show: true,
          margin: 30,
        },
      },
      data: datas.map((item) => item.name),
      axisLabel: {
        margin: 40,
        fontSize: 12,
        align: 'left',
        color: '#fff',
        rich: {
          a: {
            padding: [0, 0, 50, 10]
          },
          a1: {
            color: '#fff',
            backgroundColor: {
              image: img,
            },
            width: 30,
            height: 30,
            align: 'center',
            borderRadius: 2,
          },
          a2: {
            color: '#fff',
            backgroundColor: {
              image: img,
            },
            width: 30,
            height: 30,
            align: 'center',
            borderRadius: 2,
          },
          a3: {
            color: '#fff',
            backgroundColor: {
              image: img,
            },
            width: 30,
            height: 30,
            align: 'center',
            borderRadius: 2,
          },
          b: {
            color: '#fff',
            backgroundColor: '#02fbfd',
            width: 30,
            height: 30,
            align: 'center',
            borderRadius: 2,
          },
        },
        formatter: function (params) {
          var index = datas.map((item) => item.name).indexOf(params);
          index = index + 1;
          if (index - 1 < 3) {
            return ['{a' + index + '|' + index + '}' + '  ' + '{a|' + params + '}'].join('\n');
          } else {
            return ['{b|' + index + '}' + '  ' + '{a|' + params + '}'].join('\n');
          }
        },
      },
    },
    {
      type: 'category',
      inverse: true,
      axisTick: 'none',
      axisLine: 'none',
      axisLabel: {
        show: true,
        fontSize: 12,
        color: '#fff',
        inside: true,
        formatter: '{a|{value}%}',
        rich: {
          a: {
            padding: [0, 0, 50, 0]
          }
        }
      },
      data: datas.map((item) => item.value),
    },
  ],
  series: [
    {
      z: 2,
      name: '销量',
      type: 'bar',
      barWidth: 20,
      zlevel: 1,
      data: datas.map((item, i) => {
        return {
          value: item.value,
          itemStyle: {
            color: colorList[i],
          },
        };
      }),
      label: {
        show: true,
        position: 'right',
        color: '#fff',
        fontSize: 12,
        offset: [-30, -20],
        formatter: '{c}%'
      },
    },
    {
      name: '背景',
      type: 'bar',
      barWidth: 20,
      barGap: '-100%',
      itemStyle: {
        color: 'rgba(118, 111, 111, 0.55)',
      },
      tooltip: {
        show: false
      },
      data: maxArr,
    },
  ],
};
